mapper.select:

select id 
from customer
where id = #{id}


select email
from customer
where email = #{email}


select *
from customer
where id = #{id} and password = #{password}

mapper.update:

update customer
set id = #{id}, password = #{password}, email = #{email}
where customerNumber = #{customerNumber}


update customer
set id = #{id}, email = #{email}
where customerNumber = #{customerNumber}
